mapper.select:
select user_id, user_name, user_pw
from member_user
where user_id = #{user_id}
	and user_pw = #{user_pw}

select com_id, com_name, com_pw
from member_com
where com_id = #{com_id}
	and com_pw = #{com_pw}

mapper.update:
update member_user
	set	user_pw = ${user_pw}
	where user_name = #{user_name}

mapper.delete:
delete from member_user
where user_id = #{user_id}
	and user_pw = #{user_pw}
