mapper.select:
select board_bno, board_title, board_content, board_writer, board_regDate, board_viewCnt
from member_board
where board_bno = #{board_bno}

mapper.update:
update member_board
	set
		board_title = #{board_title},
		board_writer = #{board_writer},
		board_content = #{board_content}
	where board_bno = #{board_bno}
