mapper.select:
select *
from member_board
where board_bno = #{board_bno}

mapper.update:
update member_board
	set board_viewCnt = board_viewCnt + 1
where board_bno = #{board_bno}

update member_board
	set
		board_title = #{board_title},
		board_writer = #{board_writer},
		board_content = #{board_content}
	where board_bno = #{board_bno}

