mapper.update:
update member_board 
set	board_like = board_like + 1
where board_bno = #{board_bno}

update member_board 
set	board_like = board_like - 1
where board_bno = #{board_bno}

update member_board_want
set	board_want_like = board_want_like + 1
where board_want_bno = #{board_want_bno}

update member_board_want
set	board_want_like = board_want_like - 1
where board_want_bno = #{board_want_bno}
